import styled from '@emotion/styled';

export const Label = styled.label`
  display: flex;
  flex-direction: column;
  align-items: content;
  margin-bottom: 10px;
`;
export const Input = styled.input`
  margin-top: 6px;
  outline: none;
  border-radius: 8px;
  height: 20px;
  appearance: none;
  background-color: #a71d1d;
`;
