import styled from '@emotion/styled';

export const Items = styled.ul`
  display: flex;
  flex-direction: column;
  /* align-items: center; */
  padding: 0;
`;

export const Item = styled.li`
  /* display: flex; */
  /* justify-content: center; */
  /* align-items: flex-start; */
  /* width: 380px; */
  padding: 0;
  margin-bottom: 10px;
`;
