export const theme = Object.freeze({
  colors: {
    white: '#ffffff',
    red: '#f0230c',
    grey: '#797171',
    greyBg: '#e3e3ee',
    greyOpacity: 'rgba(74, 75, 84, 0.1)',
    primaryText: '#2b2b2b',
    buttonBg: '#ebedf3',
    buttonText: '#353e42',
    colorShadow: '#a1a6b8',
    colorBg: '#a1f0e2',
  },
  fonts: {
    secondary: "'Work Sans', sans-serif",
  },
});
