import { Formik, ErrorMessage } from 'formik';
import * as yup from 'yup';
import { Input, Button, Form } from './ContactForm.styled';
import { nanoid } from 'nanoid';

// const schema = yup.object().shape({
//   name: yup.string().required(),
//   numder: yup.string().min(7).max(7).required(),
// });

const nameInputId = nanoid();
const numberInputId = nanoid();

const initialValues = {
  name: '',
  number: '',
};

const FormikForm = ({ onSubmit, contacts }) => {
  const handleSubmit = (values, { resetForm }) => {
    const checkedName = contacts.map(contact => contact.name);

    if (checkedName.includes(values.name)) {
      alert(`${values.name} is already in contacts`);
      return;
    }

    onSubmit(values);
    resetForm();
  };

  return (
    <Formik
      initialValues={initialValues}
      // validationSchema={schema}
      onSubmit={handleSubmit}
    >
      <Form autoComplete="off">
        <label htmlFor={nameInputId}>
          <p>Name</p>
          <Input
            id={nameInputId}
            type="text"
            name="name"
            placeholder="Full name"
            pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
            title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
            required
          />
          <ErrorMessage name="name" />
        </label>
        <br />
        <label htmlFor={numberInputId}>
          <p>Number</p>
          <Input
            id={numberInputId}
            type="tel"
            name="number"
            placeholder="111-11-11"
            pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
            title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
            required
          />
          <ErrorMessage name="number" />
        </label>
        <br />
        <Button type="submit">Add contact</Button>
      </Form>
    </Formik>
  );
};

export default FormikForm;

